create table PMS_BROADCAST_RECIPIENT
(
    SEQ_ID        NUMBER(12) not null
        constraint PMS_BROADCAST_PECIPIENT_PK
            primary key,
    BROADCAST_ID  NUMBER(12) not null,
    CUSTOMER_NAME VARCHAR2(16),
    CREATE_TIME   TIMESTAMP(6) default CURRENT_TIMESTAMP,
    UPDATE_TIME   TIMESTAMP(6) default CURRENT_TIMESTAMP,
    VERSION       NUMBER(8)    default 0
)
/

comment on column PMS_BROADCAST_RECIPIENT.BROADCAST_ID is 'PMS_BROADCAST FK'
/

comment on column PMS_BROADCAST_RECIPIENT.CUSTOMER_NAME is '收件人名稱'
/

comment on column PMS_BROADCAST_RECIPIENT.CREATE_TIME is '建立時間'
/

comment on column PMS_BROADCAST_RECIPIENT.UPDATE_TIME is '更新時間'
/

comment on column PMS_BROADCAST_RECIPIENT.VERSION is '更新版本'
/

create index PMS_BROADCAST_RECIPIENT_IDX
    on PMS_BROADCAST_RECIPIENT (BROADCAST_ID)
/

